mapper.select:
select member_id from tbl_member
where member_name = #{member_name} 
	and member_email = #{member_email}

select count(*) from tbl_member
where member_id = #{member_id} 
	and member_name = #{member_name} 
	and member_email = #{member_email}

mapper.update:
update tbl_member
	set member_pw = #{new_password}
	where member_id = #{member_id}
